<script>
import StandPositionData from '@/assets/StandPositions.json'
import VueToggles from 'vue-toggles'

export default {
  components: {
    VueToggles
  },
  props: {
    isAdminPage: { type: Boolean, default: false }
  },
  computed: {
    businesses: function () {
      let b = this.$store.state.businesses.all
      return b
    },
    filteredDayBusinesses () {
      if (this.isDayOne) {
        return this.businesses.filter(business => business.days === 'Dag 1' || business.days === 'Begge dager').slice().sort((a, b) => a.standnumber - b.standnumber)
      } else {
        return this.businesses.filter(business => business.days === 'Dag 2' || business.days === 'Begge dager').slice().sort((a, b) => a.standnumber - b.standnumber)
      }
    }
  },
  data () {
    return {
      circles: [],
      names: [],
      StandPositionMap: [],
      isDayOne: true
    }
  },
  watch: {
    checked (val) {
      this.$emit('input', this.checked)
    }
  },
  created () {
    this.StandPositionMap = StandPositionData
  },
  handleTextMouseOut (standnumber) {
    const circle = document.getElementById(`circle-${standnumber}`)
    const business = document.getElementById(`business-${standnumber}`)

    circle.classList.remove('hovered')
    business.classList.remove('hovered')
  },
  changeInput () {
    this.checked = !this.checked
    this.$emit('input', !this.value)
  }
}

</script>

